import React, { Component } from "react";

import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { loginUser } from "../../actions/authActions";
import TextFieldGroup from "../common/TextFieldGroupInput";

import PropTypes from "prop-types";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      errors: {},

      preloaderStyle: "d-none"
    };
  }
  handlePreloaderStyle = newStyle => {
    this.setState({ preloaderStyle: newStyle });
  };
  handleEmailChange = event => {
    this.setState({ email: event.target.value });
  };
  handlePasswordChange = event => {
    this.setState({ password: event.target.value });
  };
  handleOnSubmit = event => {
    event.preventDefault();
    const userData = {
      email: this.state.email,
      password: this.state.password
    };
    console.log(userData);
    this.props.loginUser(userData, this.props.history);
  };
  componentWillReceiveProps(nextProps) {
    this.handlePreloaderStyle("d-none");

    if (nextProps.auth.isAuthenticated) {
      this.props.history.push("/profiles");
    }
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
  }

  componentDidMount() {
    if (this.props.auth.isAuthenticated) {
      this.props.history.push("/posts");
    }
  }

  render() {
    const { errors } = this.state;
    return (
      <div>
        <div className="container-fluid">
          <h4>Login to access the portal</h4>
          <div className="row">
            <div className="col-md-4 col-sm-4 col-xs-12"></div>
            <div className="col-md-4 col-sm-4 col-xs-12">
              <form className="form1" onSubmit={this.handleOnSubmit}>
                <TextFieldGroup
                  placeholder="Email"
                  type="email"
                  value={this.state.email}
                  onChange={this.handleEmailChange}
                  error={errors.email}
                />

                <TextFieldGroup
                  placeholder="Password"
                  type="password"
                  value={this.state.password}
                  onChange={this.handlePasswordChange}
                  error={errors.password}
                />
                <button type="submit" className="btn btn-primary mr-2">
                  Login
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

Login.propTypes = {
  LoginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(mapStateToProps, { loginUser })(withRouter(Login));
