import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";

class Navbar extends Component {
  onLogOutClick(e) {
    e.preventDefault();
    // this.props.logoutUser();
    let redirectLink;
    if (localStorage.JwtToken) {
      redirectLink = "/";
    } else {
      redirectLink = "/profiles";
    }
    this.props.logoutUser();
    window.location.href = redirectLink;
  }
  render() {
    const { isAuthenticated, user } = this.props.auth;

    const authLinks = (
      <div>
        <a
          href="/"
          className="nav-link"
          onClick={this.onLogOutClick.bind(this)}
        >
          {" "}
          User : {user.name}
          <img
            src={user.avatar}
            className="rounde-circle"
            alt={user.name}
            style={{ width: "25px", margingRight: "5px" }}
            title="Attach your image to Gravatar for it to display here"
          />
          Logout
        </a>
      </div>
    );

    // Guest links
    const guestLinks = (
      <div
        className="btn-toolbar"
        role="toolbar"
        aria-label="Toolbar with button groups"
      >
        <a className="btn btn-lg btn-info mr-2" href="/register">
          Sign Up
        </a>
        <a className="btn btn-lg btn-light" href="/login">
          Login
        </a>
      </div>
    );

    return (
      <div>
        <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
          <Link className="navbar-brand" to="/">
            Mawingu Social Network
          </Link>
          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav mr-auto">
              <li className="nav-item active">
                <Link className="nav-link" to="/">
                  Home <span className="sr-only">(current)</span>
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="profiles">
                  Profiles
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/posts">
                  Posts
                </Link>
              </li>
            </ul>
          </div>

          {isAuthenticated ? authLinks : guestLinks}
        </nav>
      </div>
    );
  }
}

Navbar.propTypes = {
  LogoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps, { logoutUser })(Navbar);
